import React, { Component } from 'react';
import {connect} from 'react-redux';
import SpotifyAxios from './../services/SpotifyAxios';
import Album from './../components/Album';
class ArtistPage extends Component {
	constructor(props){
        super(props); 
        this.state={
			artist:null,
			albums:[]
        }
    }
componentWillMount(){
let {match} = this.props;
let id = match.params.id;
SpotifyAxios.getArtists(id).then((response)=>{
	if(response!==undefined && response !== null){
	this.setState({
		artist:response.data
	})
	}
})
}
	render() {	

		let artist = {name:'', external_urls:'',geners:[],images:[{url:''}]};
		artist = this.state.artist !== null ? this.state.artist:artist;
		console.log(this.state.artist)
        return (

			<div className="panel panel-info">
				<div className="panel-heading">
		<h3 className="panel-title">{artist.name}</h3>
				</div>
				<div className="panel-body">
					<div className="col-sm-4 col-md-4">

						<blockquote style={{ marginTop: 20 }}><p></p></blockquote>
						<p>
							<i className="glyphicon glyphicon-play-circle" /><a rel="noopener noreferrer" target="_blank"> View Spotify</a><br /><br />
							<i className="glyphicon glyphicon-play-circle" />
							{/* Genres: {this.showGenres(artist.genres)} */}
						</p>
					</div>
					<div className="col-sm-8 col-md-8">
						<div className="panel panel-danger">
							<div className="panel-heading">
								<h3 className="panel-title">List Albums</h3>
							</div>
							<Album/>
						</div>
					</div>
				</div>
			</div>

)}
showGenres(genres){
	let xhml = null;
	if(genres !== null && genres.length > 0){
		xhml = genres.map((genre,index)=>{
		return <span key={index} className="badge badge-secondary">{genre}</span>
		})
	}
	return xhml
}
}
 export default ArtistPage
