export default class SpotifyFetch{
    getArtists(query){

            let url = configs.BASE_URL + "search?q=" + query + "&type=artist&limit=2&offset=0";
            let config = {
                method:'GET',
                headers: {
                    'Authorization': 'Bearer ' + configs.API_KEY          
                  }
            }
           return  fetch(url,config)
            .then((response) => response.json())

    }
    }
