import {connect} from 'react-redux';
import {actChangeQuery} from './../actions/index';

class FormSearch extends Component {
constructor(props){
    super(props);
    this.state={
        query: '',
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSearch = this.handleSearch.bind(this);
    this.handleClear = this.handleClear.bind(this); 
}

handleSearch(event) {
    this.props.handleSearch(this.state.query)
    event.preventDefault();
  }
  handleClear(event) {
   this.setState({
       query:''
   })

  }
  handleChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  }
    render() {
    let query = (this.state.query !== '')? this.state.query: this.props.query
        return ( 
            <form onSubmit={this.handleSearch} className="form-inline" >
            <div className="input-group">
                <input name="query"  onChange={this.handleChange} type="text" className="form-control" placeholder="Enter artist name ..." />
                <div className="input-group-btn">
                    <button onClick={this.handleSearch} type="button" className="btn btn-danger">Search</button>
                    <button onClick={this.handleClear} type="button" className="btn btn-warning">Clear</button>
                </div>
            </div>
        </form>
        ) 
    } 
}
const mapStateToProps = state => {
    return {
        query: state.query
    }
}
const mapDispatchToProps = (dispatch,ownProps) => {
    return {
        handleSearch: (query) => {
            dispatch(actChangeQuery)
        }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(FormSearch);
