import * as types from './../constants/ActionType';

let defaultState = '123';

const query = (state = defaultState, action) => {

	switch(action.type){
		case types.CHANGE_QUERY:
			state = action.query;
			console.log(action)
			return state;
		default:
			return state;
	}
}

export default query;
