import React, { Component } from 'react';
import {connect} from 'react-redux';
import {actChangeQuery} from './../actions/index';

class FormSearch extends Component {
    render() {
        return ( 
            <form onSubmit={this.handleSearch} className="form-inline" >
            <div className="input-group">
                <input name="query"  onChange={this.handleChange} type="text" className="form-control" placeholder="Enter artist name ..." />
                <div className="input-group-btn">
                    <button  type="button" className="btn btn-danger">Search</button>
                    <button type="button" className="btn btn-warning">Clear</button>
                </div>
            </div>
        </form>
        ) 
    } 
}
export default FormSearch;
